import React, { useState } from 'react';
import axios from 'axios';

const Login = ({ onLogin }) => {
  const [credentials, setCredentials] = useState({
    username: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e) => {
    setCredentials({
      ...credentials,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await axios.post('/api/auth/login', credentials);
      const { token, user } = response.data;
      
      // Store user info in localStorage
      localStorage.setItem('user', JSON.stringify(user));
      
      onLogin(token);
    } catch (err) {
      setError(err.response?.data?.message || 'Ã‰chec de la connexion');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-form">
      {/* BanniÃ¨re de dÃ©monstration */}
      <div style={{
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        color: 'white',
        padding: '16px',
        borderRadius: '8px',
        marginBottom: '24px',
        textAlign: 'center',
        fontSize: '14px'
      }}>
        <h3 style={{ margin: '0 0 8px 0', fontSize: '18px' }}>
          ðŸŽ¯ Projet de DÃ©monstration
        </h3>
        <p style={{ margin: '0', opacity: '0.9' }}>
          Tableau de bord administratif dÃ©veloppÃ© pour prÃ©senter mes compÃ©tences full-stack
        </p>
      </div>

      <h2>Connexion Administrateur</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Nom d'utilisateur</label>
          <input
            type="text"
            id="username"
            name="username"
            value={credentials.username}
            onChange={handleChange}
            required
            placeholder="Entrez votre nom d'utilisateur"
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Mot de passe</label>
          <div className="password-input-wrapper">
            <input
              type={showPassword ? "text" : "password"}
              id="password"
              name="password"
              value={credentials.password}
              onChange={handleChange}
              required
              placeholder="Entrez votre mot de passe"
            />
            <button
              type="button"
              className="password-toggle-btn"
              onClick={() => setShowPassword(!showPassword)}
              aria-label={showPassword ? "Masquer le mot de passe" : "Afficher le mot de passe"}
            >
              {showPassword ? (
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M17.94 17.94A10.07 10.07 0 0 1 12 20c-7 0-11-8-11-8a18.45 18.45 0 0 1 5.06-5.94M9.9 4.24A9.12 9.12 0 0 1 12 4c7 0 11 8 11 8a18.5 18.5 0 0 1-2.16 3.19m-6.72-1.07a3 3 0 1 1-4.24-4.24"/>
                  <line x1="1" y1="1" x2="23" y2="23"/>
                </svg>
              ) : (
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z"/>
                  <circle cx="12" cy="12" r="3"/>
                </svg>
              )}
            </button>
          </div>
        </div>
        {error && <div className="error-message">{error}</div>}
        <button 
          type="submit" 
          className="btn btn-primary" 
          disabled={loading}
          style={{ width: '100%', marginTop: '20px' }}
        >
          {loading ? 'Connexion en cours...' : 'Se connecter'}
        </button>
      </form>
      <div style={{ marginTop: '20px', fontSize: '14px', color: '#666' }}>
        <p>Identifiants de dÃ©monstration :</p>
        <p>Nom d'utilisateur : admin</p>
        <p>Mot de passe : admin123</p>
      </div>
    </div>
  );
};

export default Login;
